import React, { Component } from 'react'
import Dialog from '@material-ui/core/Dialog';
import Imageupload from 'react-images-upload'
import Resizer from 'react-image-file-resizer';

class ImageUploader extends Component {

    constructor(props) {
        super(props)

        this.state = {
            uploader: this.props.props,

            fetchImg: ""
        }
    }

    handleClose = () => {

        this.setState({
            uploader: !this.state.uploader
        })
        this.props.uploadRefresh()
    }

    onDrop = (event) => {

       const file = event[0]
       this.resizingImage(file)

        // var fetchData = new FileReader()
        // fetchData.readAsDataURL(event[0])

        // fetchData.onload = async (event) => {
        //     const url = event.target.result

        //    

        //        await this.setState({
        //             fetchImg: url 
        //         })
        // }
    }

    resizingImage = (data) => {

        Resizer.imageFileResizer(
            data,
            500,
            500,
            'JPEG',
            50,
            0,
            uri => {
                     this.setState({
                    fetchImg: uri 
        })
            },
            'base64'
        );



    }




    render() {


        return (
            <div>
                <Dialog open={this.state.uploader} onClose={this.handleClose}>

                    <div className="ImagePicker">

                        <Imageupload
                            withIcon={true}
                            buttonText='Choose images'
                            onChange={event => this.onDrop(event)}
                            imgExtension={['.jpg', '.gif', '.png', '.gif', '.jpeg']}
                            maxFileSize={52421000}
                        />
                        <div>

                            <div style={{ height: "100px", width: "100px" }}>

                                <img src={this.state.fetchImg} style={{ height: "100px", width: "100px" }} />
                            </div>
                            <button onClick={this.handleClose}>
                                close
                            </button>
                        </div>
                    </div>

                </Dialog>
            </div>
        )
    }
}
export default ImageUploader
